import { useState, useEffect } from 'react';
import Stomp from 'stompjs';

function App() {
  const [message, setMessage] = useState("");
  const [client, setClient] = useState(null);
  const [recMessage, fillRecMessage] = useState([]);

  let stompClient;

  useEffect(() => {
    if (!client) {
      console.log("Connecting to queue  ...");
      queueConnect();
    }
  }, [client]);

  function queueConnect() {
    var ws = new WebSocket('ws://192.168.56.102:15674/ws');
                            // ws://127.0.0.1:15674/ws

    const headers = {
      'login': 'admin',
      'passcode': 'admin',
      'durable': 'true',
      'auto-delete': 'false',
      // 'type':'fanout'
    };

    stompClient = Stomp.over(ws);
    stompClient.connect(headers, successConnection, failedConnection);
    setClient(stompClient);
  }

  function successConnection(frame) {
    console.log("Success connection ...");
    //const subscriptionQ = stompClient.subscribe('/queue/myQueue', receivedMessage)
    const subscriptionX = stompClient.subscribe('/exchange/myExchange', receivedMessage)
  }
  function failedConnection(frame) {
    console.log("Failed connection ...");
  }

  function receivedMessage(message) {
    console.log(message);
    fillRecMessage(oldArray => [...oldArray, message.body]);
  }


  function sendMessage() {
    console.log("Se envio: ", message);
    if (client && message.trim() !== '') {
      //client.send("/queue/myQueue", {}, message);
      client.send("/exchange/myExchange", {}, message);
      console.log('Mensaje mandado ...');
    }
    limpiar();
  }

  function limpiar() {
    var inputNombre = document.getElementsById("message");
    inputNombre.value = "";
  }

  return (
    <div className="container col-sm-6">
      <header >
        <h1>
          Mensajeador
        </h1>
      </header>
      <div className="input-group fluid">
        <label htmlFor="messageInput">Mensaje</label>
        <input type="text" id="messageInput" placeholder="Escribe tu mensaje" onChange={(event) => setMessage(event.target.value)} />
      </div>
      <button className="primary" onClick={() => { sendMessage() }}>Enviar</button>

      <div className="row">
        <div className="col-sm-12">
          <div className="card fluid">
            <div className="section"><h3>Mensajes recibidos</h3></div>
            <div className="section">
              {recMessage.map((item, idx) => {
                return (
                  <p> <mark class="tag"> {item} </mark> </p>
                )
              })}
            </div>
          </div>
        </div>
      </div>

    </div>
  );
}

export default App;
