import { useState, useEffect } from 'react';
import Stomp from 'stompjs';

function App() {
  const [message, setMessage] = useState("");
  const [userid, setUserId] = useState('USID')
  const [subscriptionObject, setSubscrition] = useState('USID')
  const [client, setClient] = useState(null);
  const [recMessage, fillRecMessage] = useState([]);

  let stompClient;

  useEffect(() => {
    if (!client) {
      console.log("Connecting to queue  ...");
      queueConnect();
    }
  }, [client]);

  function queueConnect() {
    var ws = new WebSocket('ws://192.168.56.102:15674/ws');
                            // ws://127.0.0.1:15674/ws

    const headers = {
      'login': 'admin',
      'passcode': 'admin',
      'durable': 'true',
      'auto-delete': 'false',
      // 'type':'fanout'
    };

    stompClient = Stomp.over(ws);
    stompClient.connect(headers, successConnection, failedConnection);
    setClient(stompClient);
  }

  function successConnection(frame) {
    var userIdTemp = (Math.floor(Math.random() * 1000 + 1));
    setUserId(userid + userIdTemp);
    console.log("Success connection ...");
    //const subscriptionQ = stompClient.subscribe('/queue/myQueue', receivedMessage)
    const subscriptionX = stompClient.subscribe('/exchange/myExchange', receivedMessage, {id: userIdTemp});
    setSubscrition(subscriptionX);
    console.log("+++", subscriptionX.id);
  }
  function failedConnection(frame) {
    console.log("Failed connection ...");
  }

  function receivedMessage(message) {
    console.log(message);
    console.log(message.body);
    fillRecMessage(oldArray => [...oldArray, message.headers.senderId + ": " + message.body]);
  }


  function sendMessage() {
    console.log("Send message clicked ...", message);
    if(client && message.trim() !== '') {
      // client.send("/queue/myQueue", {}, message);
      client.send("/exchange/myExchange", {senderId: userid}, message);
      console.log('Message sent ...');
    }
    limpiar();
  }

  function limpiar() {
    var inputNombre = document.getElementById("messageInput");
    inputNombre.value = "";
  }

  function changeUserID() {
    console.log("Changing id from ", subscriptionObject);
    if (subscriptionObject) {
      subscriptionObject.unsubscribe();
      const subscriptionX = client.subscribe('/exchange/myExchange', receivedMessage, {id: userid})
      setSubscrition(subscriptionX);
      console.log("+++", subscriptionX.id);
    }
  }

  return (
    <div className="container col-sm-6">
      <header>
        <h3>
          Mensajeador
        </h3>
      </header>
      <div className="input-group fluid">
        <label htmlFor="userId">UserId</label>
        <input type="text" id="userId" placeholder="user id" onChange={(event) => setUserId(event.target.value)} />
        <button className="tertiary" onClick={() => { changeUserID() }}>Subscribe</button>
      </div>
      <div className="input-group fluid">
        <label htmlFor="messageInput">Message</label>
        <input type="text" id="messageInput" placeholder="input message to send" onChange={(event) => setMessage(event.target.value)} />
      </div>
      <button className="primary" onClick={() => { sendMessage() }}>Send messsage</button>
      <div className="row">
        <div className="col-sm-12">
          <div className="card fluid">
            <div className="section"><h3>Received Messages</h3></div>
            <div className="section" style={{height: '300px', overflowY: 'scroll'}}>
              {recMessage.map((item, idx) => {
                // console.log(item);
                var regex = /(^[^:]+): /g;
                var item_user_id = "";
                var item_msg = item.replace(regex,"");
                var matches, arr_group = [];
                while (matches = regex.exec(item)) {
                  arr_group.push(matches[1]);
                }
                if (arr_group.length > 0) 
                  item_user_id = arr_group[0];

                var it_is_me = (userid == item_user_id);

                if (!it_is_me) {
                  return(
                    <div key={idx} className="bubbleWrapper">
                      <div className="inlineContainer">
                        <img className="inlineIcon" src="https://cdn1.iconfinder.com/data/icons/ninja-things-1/1772/ninja-simple-512.png"/>
                        <div className="otherBubble other">
                          {item_msg}
                        </div>
                      </div>
                      <span className="other">{item_user_id}</span>
                    </div>
                  )
                } else {
                  return(
                    <div key={idx} className="bubbleWrapper">
                      <div className="inlineContainer own">
                        <img className="inlineIcon" src="https://www.pinclipart.com/picdir/middle/205-2059398_blinkk-en-mac-app-store-ninja-icon-transparent.png" />
                        <div className="ownBubble own">
                          {item_msg}
                      </div>
                      </div><span className="own">me</span>
                    </div>
                  )
                }
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
